import { useState } from "react";
import mealsImage from '../../assets/meals.jpg';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const Register = () => {
  const history = useNavigate;
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [address, setAddress] = useState('');

  const postData = () =>{
    axios.post("http://127.0.0.1:3000/save_data" , {name,email,password,address})
    .then(() => {
      history.push("/Create")
    })
    
  }
  return (
    <div>
      <div className="Img">
      <img src={mealsImage} alt='A table full of delicious food!' />
      </div>
    <div className="create">
      <form className="form_login">
        <h1>Register</h1>
        <label>Name</label>
        <input 
          type="text" 
          required 
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label>Address</label>
        <textarea rows='10'
          required
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        ></textarea>
           <label>Email ID</label>
        <input 
          type="text" 
          required 
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
           <label>Password</label>
        <input 
          type="Password" 
          required 
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <a class="btn" href="http://localhost:3000/" target="_self"><button type="button">Login</button></a>
        <button type="button" onClick={postData}>Submit</button>
      </form>
    </div>
    </div>
    
  );
}
 
export default Register;
